mapper.select:
select a.*,rownum from (select ct.*,cu.nickname authorName from CommunityTopic ct join CommunityUser cu on cu.id = ct.authorid where ct.isBest = 1 and ct.Status = 1
and ct.forumId = #{forumId}
order by updatetime desc) a where rownum < 6

select * from ( select a.*,row_number()over(order by id) num from (select ct.*,cu.nickname authorName from CommunityTopic ct join CommunityUser cu on cu.id = ct.authorid where ct.Status = 1
and ct.isBest = #{isBest}
and ct.title like '%'|| #{title} ||'%'
and ct.forumId = #{forumId}
order by ct.updatetime desc) a) b where num >= (#{currentPage}-1)*#{row}+1 and num <= (#{row}*#{currentPage})

select count(id) from CommunityTopic where Status = 1

    and isBest = #{isBest}


    and forumId = #{forumId}


    and title like '%'|| #{title} ||'%'
